/**
 * Module dependencies.
 */

var hasDom = typeof window !== 'undefined';
var Tween = hasDom ? require('tween') : function() {};
var raf = hasDom ? require('raf') : function() {};

/**
 * Expose `scrollTo`.
 */

module.exports = scrollTo;

/**
 * Scroll to `(x, y)`.
 *
 * @param {Element} container An element that has scrollTop/scrollLeft properties
 * @param {Number} x
 * @param {Number} y
 * @param {Object} options
 * @api public
 */

function scrollTo(container, x, y, options) {
  options = options || {};

  // start position
  var start = scroll(container);

  // setup tween
  var tween = Tween(start)
    .ease(options.ease || 'out-circ')
    .to({ top: y, left: x })
    .duration(options.duration || 1000);

  // scroll
  tween.update(function(o){
    container.scrollTop = o.top || 0
    container.scrollLeft = o.left || 0
  });

  // handle end
  tween.on('end', function(){
    animate = function(){};
  });

  // animate
  function animate() {
    raf(animate);
    tween.update();
  }

  animate();
  
  return tween;
}

/**
 * Return scroll position.
 *
 * @return {Object}
 * @api private
 */

function scroll(container) {
  var y = container.scrollTop;
  var x = container.scrollLeft;
  return { top: y, left: x };
}
